echo; ifconfig | grep inet | grep -v 127.0.0.1 | egrep -o "addr:"[0-9]+\.[0-9]+\.[0-9]+\.[0-9]+ | sed 's/addr://g' | sort | uniq; echo; echo "Hostname: `hostname`"; echo "SSH: ssh `hostname -i` -l root -p "`netstat -plan | grep ssh | grep -m1 LISTEN | awk '{print $4}' | sed 's/:/ /g' | awk '{print $NF}'`; echo; for i in `egrep "ns[0-9]+\.dizinc" /var/named/*.db | grep NS | awk '{print $NF}' |sort|uniq`; do echo -n $i" -> "; echo `dig A $i +short`; done
