echo -n "# HTTP #"; /etc/init.d/httpd status |grep -v localhost|grep -v Scoreboard; echo;echo "# MYSQL #"; mysqladmin proc stat; /etc/init.d/mysql status; echo;echo "# DNS #"; ns=$(/scripts/setupnameserver --current | awk '{print $NF}'); if [ $ns == "bind" ]; then /etc/init.d/named status | grep "server\|named\|zones" --color=never; else /etc/init.d/nsd status; echo "nsd is running..."; fi; echo;echo "# CPANEL #"; /etc/init.d/cpanel status; echo;echo "# CSF #"; /etc/init.d/csf status|grep --color=none csf && echo -n "Total: " && /etc/init.d/csf status | wc -l; echo; echo "# FTP #"; if [ -f /etc/init.d/pure-ftpd ]; then /etc/init.d/pure-ftpd status; fi; if [ -f /etc/init.d/proftpd ]; then /etc/init.d/proftpd status; echo; fi;  echo "# EXIM #"; /etc/init.d/exim status; echo -n "Exim Queue: "; exim -bpc; echo; echo "Process Snapshot?"; read derp; ps axuf
